const buyButtons=document.querySelectorAll(".product-actions-buy"),toTheBasketModal=document.querySelector(".to-basket-modal");buyButtons.forEach(function(o){o.addEventListener("click",function(o){o.preventDefault(),toTheBasketModal.classList.add("modal-show")})});const closeModalButtons=document.querySelectorAll(".button-close-modal");function closeModal(o,e){o.preventDefault(),e.classList.remove("modal-show"),e.classList.remove("modal-error")}closeModalButtons.forEach(function(o){o.addEventListener("click",function(e){closeModal(e,o.closest(".modal"))})}),window.addEventListener("keydown",function(o){if("Escape"===o.code){const e=document.querySelector(".modal.modal-show");e&&closeModal(o,e)}});